punctuation = '!"#$%&\'()*+,./:;<=>?@[\\]^_`{|}~-'
specialchar_re = re.compile('(%s)' % '|'.join(specialchar_dic.keys()))
nlp = spacy.load("en_core_web_sm")
cachedStopWords_en = stopWordsEN()
cachedStopWordsSA_en = stopWordsSAEN()
contractions_en = contractionsEN()
contractions_re_en = re.compile('(%s)' % '|'.join(contractions_en.keys()))


class Tokenization:
    def removeScripts(self, text):
        return re.sub(r"(?is)(<script[^>]*>)(.*?)(</script>)", '', text)

    def removeTags(self, text):
        return re.sub(re.compile('<.*?>'),'', text)

    def removeURLs(self, text):
        return re.sub(r"http\S+", "", text)

    def expandContractions(self, text):
        def replace(match):
            return contractions_en[match.group(0)]
        return contractions_re_en.sub(replace, text)

    def removeStopWords(self, text, keep_negations=True):
        # for sentiment analysis we keep negations
        if keep_negations:
            return ' '.join([word for word in text.split() if word not in cachedStopWordsSA_en])
        else:
            return ' '.join([word for word in text.split() if word not in cachedStopWords_en])

    def removePunctuation(self, text, punctuation = punctuation):
        for c in punctuation:
            text = text.replace(c, ' ')
        return text

    def replaceUTF8Char(self, text, specialchars=specialchar_dic):
        def replace(match):
            return specialchars[match.group(0)]
        return specialchar_re.sub(replace, text)

    def createCorpus(self, text, remove_punctuation=True, remove_stopwords=True, keep_negations=True, return_tokens=True):
        corpus = []
        try:
            text = self.replaceUTF8Char(self.removeURLs(self.removeTags(self.removeScripts(text)))).replace("\n", " ")
            text = self.expandContractions(text)
            doc = nlp(text)
            wordsSet = set(words.words())

            processed_text =  ' '.join([t.lemma_ if t.lemma_ != '-PRON-' else t.text if not t.ent_type_ else t.text for t in doc])
            processed_text = processed_text.replace("\s\s+", ' ')

            doc = nlp(processed_text.lower())

            for sentence in doc.sents:
                sent = str(sentence.text)
                if len(sent) == 0:
                    continue
                if remove_punctuation:
                    sent = self.removePunctuation(text=sent)
                if remove_stopwords:
                    sent = self.removeStopWords(text=sent, keep_negations=keep_negations)
                sent = sent.lower().split()
                if sent:
                    clean_sent = []
                    for token in sent:
                        if ((len(token) >= 3 and token in wordsSet) or (token == 'no' and keep_negations)): 
                            clean_sent.append(token.lower())
                    if len(clean_sent) > 0:
                        if return_tokens:
                            corpus += clean_sent
                        else:
                            corpus.append(clean_sent)
        except Exception as exp:
            print('exception=', str(exp))
            print('text=', text)
        # print(corpus)
        return corpus

    def __del__(self):
        print("Destructor Tokenization")



